template.template_md:
{% block content %}
<style>
  @page {
    margin: 0.5in;
    size: letter landscape;
    /* or A4 landscape */
  }

  /* —— Only per‑report row & badge colors —— */
  .status-ready {
    background-color: var(--status-ready-bg);
  }

  .status-not-ready {
    background-color: var(--status-not-ready-bg);
  }

  .status-unknown {
    background-color: var(--status-unknown-bg);
  }

  .badge-ready {
    background-color: var(--status-ready-badge);
  }

  .badge-not-ready {
    background-color: var(--status-not-ready-badge);
  }

  .badge-unknown {
    background-color: var(--status-unknown-badge);
    color: #333;
  }

  .badge-already {
    background-color: var(--badge-already-bg);
  }
</style>
<div class="report-header">
  <div class="header-logo">
    <img src="https://github.com/nuvemquery/reporting-templates/blob/master/assets/gbtnavy%20(256).png?raw=true" alt="Amidaware Logo" style="width: 80px; height: auto;">
  </div>
  <div class="header-titles">
    <h1>Windows 11 Compatibility Report</h1>
    <h2>Client: {{ client.name }}</h2>
  </div>
  <div class="header-date">
    <h2>Report Date:<br>{{ report_run_timestamp.strftime('%B %d, %Y') }}</h2>
  </div>
</div>

<div class="section">
  <table class="report-table">
    <thead>
      <tr>
        <th>Client</th>
        <th>Site</th>
        <th>Device Name</th>
        <th>User</th>
        <th>Operating System</th>
        <th style="text-align: center;">Windows 11 Compatibility</th>
      </tr>
    </thead>
    <tbody>
      {% set not_ready_devices, ready_devices, already_devices = [], [], [] %}
      {% for device in data_sources.agentsList %}
      {% if device.plat and device.plat|lower == "windows" %}
      {% if device.custom_fields.Windows11Comp %}
      {% if device.custom_fields.Windows11Comp == "Windows 11 Ready" %}
      {% set _ = ready_devices.append(device) %}
      {% elif device.custom_fields.Windows11Comp == "Already Windows 11" %}
      {% set _ = already_devices.append(device) %}
      {% else %}
      {% set _ = not_ready_devices.append(device) %}
      {% endif %}
      {% else %}
      {% set _ = not_ready_devices.append(device) %}
      {% endif %}
      {% endif %}
      {% endfor %}
      {% set sorted_devices = (not_ready_devices | sort(attribute='hostname') | sort(attribute='site__name')) +
      (ready_devices | sort(attribute='hostname') | sort(attribute='site__name')) +
      (already_devices | sort(attribute='hostname') | sort(attribute='site__name')) %}

      {% for device in sorted_devices %}
      {% set row_class = 'status-unknown' %}
      {% set badge_class = 'badge-unknown' %}
      {% set status_text = 'Update custom_field' %}
      {% if device.custom_fields.Windows11Comp %}
      {% if device.custom_fields.Windows11Comp == "Windows 11 Ready" %}
      {% set row_class = 'status-ready' %}
      {% set badge_class = 'badge-ready' %}
      {% set status_text = 'Ready' %}
      {% elif device.custom_fields.Windows11Comp == "Already Windows 11" %}
      {% set row_class = 'status-ready' %}
      {% set badge_class = 'badge-already' %}
      {% set status_text = 'Already Windows 11' %}
      {% else %}
      {% set row_class = 'status-not-ready' %}
      {% set badge_class = 'badge-not-ready' %}
      {% set status_text = 'Not Ready - ' ~ device.custom_fields.Windows11Comp %}
      {% endif %}
      {% endif %}

      <tr class="{{ row_class }}">
        <td>{{ device.site__client__name or 'N/A' }}</td>
        <td>{{ device.site__name or 'N/A' }}</td>
        <td>
          <b>{{ device.hostname or 'N/A' }}</b><br>
          {% if device.last_seen %}
          <small {% if (report_run_timestamp - device.last_seen).days > 30 %} class="text-danger" {% endif %}>
              Last Seen: {{ device.last_seen.astimezone(ZoneInfo('America/New_York')).strftime('%Y-%m-%d %H:%M:%S') }}
            </small>
          {% else %}
          <small>N/A</small>
          {% endif %}
        </td>
        <td>{{ device.last_logged_in_user or 'N/A' }}</td>
        <td style="word-wrap: break-word;">{{ device.operating_system or 'N/A' }}</td>
        <td style="text-align: center;">
          <span class="status-badge {{ badge_class }}">{{ status_text }}</span>
        </td>
      </tr>
      {% endfor %}
    </tbody>
  </table>
</div>
{% endblock %}
